import "./App.css";
import portada from "./assets/lugo-04.webp";
import ReactWhatsapp from "react-whatsapp";
import { Popover, Image, Input } from "antd";
import { useState } from "react";

function App() {
  const text = <span>Que deseas pedir?</span>;
  const { TextArea } = Input;

  const [postData, setPostData] = useState({
    waiter: "",
    message: "",
  });

  const [visible, setVisible] = useState(false);
  const [visible1, setVisible1] = useState(false);
  const [visible2, setVisible2] = useState(false);

  const content = (
    <div>
      <>
        <h5>Elige al mesero que te atendio?</h5>
        <select
          name="waiter"
          onChange={(e) => {
            setPostData({ ...postData, waiter: e.target.value });
          }}
        >
          <option value="" selected>
            Selecciona...
          </option>
          <option value="3137137829">Diego Alejandro Lugo Tellez</option>
          <option value="3116877470">Juan Guillermo</option>
          <option value="3148133470">Jhon Deiby Montoya</option>
        </select>
        <br />
        <br />
        <TextArea
          placeholder="Describenos que deseas"
          allowClear
          name="message"
          onChange={(e) =>
            setPostData({
              ...postData,
              message: e.target.value,
            })
          }
        />
        <div className="sendMessage">
          <ReactWhatsapp
            number={`+57${postData.waiter}`}
            message={postData.message}
          />
        </div>
        <h6>Se realizara el envio por medio de WhatsApp</h6>
      </>
    </div>
  );

  return (
    <>
      <div class="carousel-item active">
        <img class="d-block" src={portada} alt="First slide" />
      </div>
      <div className="buttonPopover">
        <Popover
          placement="bottom"
          title={text}
          content={content}
          trigger="click"
        >
          <button className="ButtonToggle">Antojado?</button>
        </Popover>
      </div>
    </>
  );
}

export default App;
