import logo from "./logo.svg";
import "./App.css";
import ReactWhatsapp from "react-whatsapp";
import { Popover, Button, Input, Select } from "antd";
import { useState } from "react";

function App() {
  const text = <span>Que deseas pedir?</span>;
  const { TextArea } = Input;

  const { Option } = Select;

  const [postData, setPostData] = useState({
    waiter: "",
    message: "",
  });

  console.log(postData);

  const content = (
    <div>
      <>
        <Input.Group compact>
          <select
            defaultValue="Quien te atendio?"
            onChange={(e) => {
              setPostData({ ...postData, waiter: e.target.value });
            }}
          >
            <option value="3137137829">Diego Alejandro Lugo Tellez</option>
            <option value="3148133470">Jhon Deiby Montoya</option>
          </select>
        </Input.Group>
        <br />
        <br />
        <TextArea
          placeholder="textarea with clear icon"
          allowClear
          name="message"
          onChange={(e) =>
            setPostData({
              ...postData,
              message: e.target.value,
            })
          }
        />
        <ReactWhatsapp
          number={`+57${postData.waiter}`}
          message={postData.message}
        />
      </>
    </div>
  );

  const buttonWidth = 70;

  return (
    <>
      <Popover
        placement="bottom"
        title={text}
        content={content}
        trigger="click"
      >
        <Button>Antojado?</Button>
      </Popover>
    </>
  );
}

export default App;
