import logo from "./logo.svg";
import "./App.css";
import ReactWhatsapp from "react-whatsapp";
import { Popover, Image, Button, Input, Select, Card } from "antd";
import { useState } from "react";

function App() {
  const text = <span>Que deseas pedir?</span>;
  const { TextArea } = Input;
  const { Option } = Select;
  const { Meta } = Card;

  const [postData, setPostData] = useState({
    waiter: "",
    message: "",
  });

  const [visible, setVisible] = useState(false);

  const content = (
    <div>
      <>
        <h5>Elige al mesero que te atendio?</h5>
        <select
          name="waiter"
          onChange={(e) => {
            setPostData({ ...postData, waiter: e.target.value });
          }}
        >
          <option value="3137137829">Diego Alejandro Lugo Tellez</option>
          <option value="3148133470">Jhon Deiby Montoya</option>
        </select>
        <br />
        <br />
        <TextArea
          placeholder="Describenos que deseas"
          allowClear
          name="message"
          onChange={(e) =>
            setPostData({
              ...postData,
              message: e.target.value,
            })
          }
        />
        <div className="sendMessage">
          <ReactWhatsapp
            number={`+57${postData.waiter}`}
            message={postData.message}
          />
        </div>
        <h6>Se realizara el envio por medio de WhatsApp</h6>
      </>
    </div>
  );

  const buttonWidth = 70;

  return (
    <>
      <div className="content">
        <Card
          hoverable
          style={{ width: 240 }}
          cover={
            <>
              <div style={{ display: "none" }}>
                <Image
                  preview={{ visible: false }}
                  src="https://gw.alipayobjects.com/zos/antfincdn/LlvErxo8H9/photo-1503185912284-5271ff81b9a8.webp"
                  onClick={() => setVisible(true)}
                />
                <Image.PreviewGroup
                  preview={{
                    visible,
                    onVisibleChange: (vis) => setVisible(vis),
                  }}
                >
                  <Image src="https://gw.alipayobjects.com/zos/antfincdn/LlvErxo8H9/photo-1503185912284-5271ff81b9a8.webp" />
                  <Image src="https://gw.alipayobjects.com/zos/antfincdn/cV16ZqzMjW/photo-1473091540282-9b846e7965e3.webp" />
                  <Image src="https://gw.alipayobjects.com/zos/antfincdn/x43I27A55%26/photo-1438109491414-7198515b166b.webp" />
                </Image.PreviewGroup>
              </div>
            </>
          }
        >
          <Meta title="" description="www.instagram.com" />
        </Card>
      </div>
      <div className="buttonPopover">
        <Popover
          placement="bottom"
          title={text}
          content={content}
          trigger="click"
        >
          <button className="ButtonToggle">Antojado?</button>
        </Popover>
      </div>
    </>
  );
}

export default App;
