import logo from "./logo.svg";
import "./App.css";
import ReactWhatsapp from "react-whatsapp";
import { Popover, Button, Input, Select, Card } from "antd";
import { useState } from "react";

function App() {
  const text = <span>Que deseas pedir?</span>;
  const { TextArea } = Input;
  const { Option } = Select;
  const { Meta } = Card;

  const [postData, setPostData] = useState({
    waiter: "",
    message: "",
  });

  const content = (
    <div>
      <>
        <h5>Elige al mesero que te atendio?</h5>
        <select
          name="waiter"
          onChange={(e) => {
            setPostData({ ...postData, waiter: e.target.value });
          }}
        >
          <option value="3137137829">Diego Alejandro Lugo Tellez</option>
          <option value="3148133470">Jhon Deiby Montoya</option>
        </select>
        <br />
        <br />
        <TextArea
          placeholder="Describenos que deseas"
          allowClear
          name="message"
          onChange={(e) =>
            setPostData({
              ...postData,
              message: e.target.value,
            })
          }
        />
        <div className="sendMessage">
          <ReactWhatsapp
            number={`+57${postData.waiter}`}
            message={postData.message}
          />
        </div>
        <h6>Se realizara el envio por medio de WhatsApp</h6>
      </>
    </div>
  );

  const buttonWidth = 70;

  return (
    <>
      <div className="content">
        <Card
          hoverable
          style={{ width: 240 }}
          cover={
            <img
              alt="example"
              src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png"
            />
          }
        >
          <Meta title="Europe Street beat" description="www.instagram.com" />
        </Card>
      </div>
      <Card
        hoverable
        style={{ width: 240 }}
        cover={
          <img
            alt="example"
            src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png"
          />
        }
      >
        <Meta title="Europe Street beat" description="www.instagram.com" />
      </Card>
    </>
  );
}

export default App;
