import logo from "./logo.svg";
import "./App.css";
import ReactWhatsapp from "react-whatsapp";
import { Popover, Image, Button, Input, Select, Card } from "antd";
import { useState } from "react";

function App() {
  const text = <span>Que deseas pedir?</span>;
  const { TextArea } = Input;
  const { Option } = Select;
  const { Meta } = Card;

  const [postData, setPostData] = useState({
    waiter: "",
    message: "",
  });

  const [visible, setVisible] = useState(false);

  const content = (
    <div>
      <>
        <h5>Elige al mesero que te atendio?</h5>
        <select
          name="waiter"
          onChange={(e) => {
            setPostData({ ...postData, waiter: e.target.value });
          }}
        >
          <option value="3137137829">Diego Alejandro Lugo Tellez</option>
          <option value="3148133470">Jhon Deiby Montoya</option>
        </select>
        <br />
        <br />
        <TextArea
          placeholder="Describenos que deseas"
          allowClear
          name="message"
          onChange={(e) =>
            setPostData({
              ...postData,
              message: e.target.value,
            })
          }
        />
        <div className="sendMessage">
          <ReactWhatsapp
            number={`+57${postData.waiter}`}
            message={postData.message}
          />
        </div>
        <h6>Se realizara el envio por medio de WhatsApp</h6>
      </>
    </div>
  );

  const buttonWidth = 70;

  return (
    <>
      <div className="content">
        <Card
          hoverable
          style={{ width: 240 }}
          cover={
            <>
              <Image
                preview={{ visible: false }}
                src="https://i.postimg.cc/43gT8BcS/CAFE-01.jpg"
                onClick={() => setVisible(true)}
              />

              <div style={{ display: "none" }}>
                <Image.PreviewGroup
                  preview={{
                    visible,
                    onVisibleChange: (vis) => setVisible(vis),
                  }}
                >
                  <Image src="https://i.postimg.cc/43gT8BcS/CAFE-01.jpg" />
                  <Image src="https://i.postimg.cc/02KRPLd4/CAFE-02.jpg" />
                </Image.PreviewGroup>
              </div>
            </>
          }
        >
          <img
            src="https://i.postimg.cc/6qdrh6xS/CAPITAN-PREMIERE2-LOGO.png"
            className="logo"
          />
        </Card>
        <Card
          hoverable
          style={{ width: 240 }}
          cover={
            <>
              <Image
                preview={{ visible: false }}
                src="https://i.postimg.cc/wBmQBQbg/CAFE-03.jpg"
                onClick={() => setVisible(true)}
              />

              <div style={{ display: "none" }}>
                <Image.PreviewGroup
                  preview={{
                    visible,
                    onVisibleChange: (vis) => setVisible(vis),
                  }}
                >
                  <Image src="https://i.postimg.cc/wBmQBQbg/CAFE-03.jpg" />
                  <Image src="https://i.postimg.cc/QdRcvWNP/CAFE-04.jpg" />
                </Image.PreviewGroup>
              </div>
            </>
          }
        >
          <img
            src="https://i.postimg.cc/6qdrh6xS/CAPITAN-PREMIERE2-LOGO.png"
            className="logo"
          />
        </Card>
        <Card
          hoverable
          style={{ width: 240 }}
          cover={
            <>
              <Image
                preview={{ visible: false }}
                src="https://i.postimg.cc/L6P9Nq7h/CAFE-05.jpg"
                onClick={() => setVisible(true)}
              />

              <div style={{ display: "none" }}>
                <Image.PreviewGroup
                  preview={{
                    visible,
                    onVisibleChange: (vis) => setVisible(vis),
                  }}
                >
                  <Image src="https://i.postimg.cc/L6P9Nq7h/CAFE-05.jpg" />
                  <Image src="https://i.postimg.cc/JntmJtZr/CAFE-06.jpg" />
                </Image.PreviewGroup>
              </div>
            </>
          }
        >
          <img
            src="https://i.postimg.cc/6qdrh6xS/CAPITAN-PREMIERE2-LOGO.png"
            className="logo"
          />
        </Card>
      </div>
      <div className="buttonPopover">
        <Popover
          placement="bottom"
          title={text}
          content={content}
          trigger="click"
        >
          <button className="ButtonToggle">Antojado?</button>
        </Popover>
      </div>
    </>
  );
}

export default App;
